import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios'
import { useLocation } from 'react-router'

import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import TeamTag from '../components/TeamTag'
import TeamForm from '../components/TeamForm'
import "../client.scss"
import Header from "../components/Header";

const userAxios = axios.create()
userAxios.interceptors.request.use(config => {
    const token = localStorage.getItem("token")
    config.headers.Authorization = `Bearer ${token}`
    return config
})

const Client = () => {

    const location = useLocation()
    console.log(location.state.client)
    if(location.state.client === null){
        return client = useContext(UserContext)
    }

    const [commissioner, setCommissioner] = useState({})
    const [teams, setTeams] = useState([])

    function getTeams(){
        userAxios.get(`/api/team/client/${client._id}`)
            .then(res => {
                setTeams(res.data)
            })
            .catch(err => console.log(err))
    }

    function createTeam(e, inputs){
        e.preventDefault()

        userAxios.post(`/api/team/client/${client._id}`, inputs)
            .then(res => {
                setTeams(prevTeams => ([...prevTeams, res.data]))
            })
            .catch(err => console.log(err))
    }

    function getCommissioner(){
        userAxios.get(`/api/user/${client.commissioner}`)
            .then(res => setCommissioner(res.data))
            .catch(err => console.log(err))
    }

    useEffect(() => {

        getCommissioner()
        getTeams()

    }, [])

    return (
        <>
        <Header />
        <div className="client-header">
            <p><ArrowBackIosIcon /> {client.name}</p>
        </div>
        <div className="comm-container">
            <p>Commissioner: {commissioner.firstName === undefined ? 'No commissioner assigned' : `${commissioner.firstName} ${commissioner.lastName}`}</p>
            <label>Add a Team</label>
            <TeamForm submit={createTeam}/>
            <div style={{margin: '16px'}}>
                {teams.map(team => <TeamTag key={team._id} team={team} client={client.name} name={team.name} avatar={team.avatar} />)}
                <Link to='/createteam' 
                    style={{
                        borderRadius: '50%', 
                        boxShadow: '0 0 10px blue', 
                        backgroundColor: 'grey',
                        color: 'white', 
                        display: 'grid', 
                        fontSize: '24px', 
                        height: '145px',
                        margin: '16px', 
                        placeItems: 'center', 
                        textAlign: 'center', 
                        textDecoration: 'none',
                        width: '145px',
                    }}>
                + NEW TEAM
                </Link>
            </div>
        </div>
        </>
    )
}

export default Client
