import { useEffect, useContext, useState } from 'react'
import {Link} from "react-router-dom"
import axios from "axios"

import SubHeader from "../components/SubHeader"
import { UserContext } from "../context/UserProvider"
import EventCard from '../components/EventCard'

const userAxios = axios.create()
userAxios.interceptors.request.use(config => {
    const token = localStorage.getItem("token")
    config.headers.Authorization = `Bearer ${token}`
    return config
})

const Contests = () => {

    const {user: {client, role}} = useContext(UserContext)

    const [contests, setContests] = useState([])

    function getContests(){
        if(role === "admin"){
            console.log('admin')
            userAxios.get("/event")
                .then(res => showContests(res.data))
                .catch(err => console.log(err))
        } else {
            console.log('general')
            userAxios.get(`/event/${client}`)
                .then(res => showContests(res.data))
                .catch(err => console.log(err))
        }
    }

    function showContests(events){
        const contests = events.filter(event => event.type === "contest")
        setContests(contests)
    }

    useEffect(() => {
        getContests()
    }, [])

    return (
        <>
            <SubHeader header1={"GAMiNG"} header2={"CONTESTS"} color={"#1c3557"} imgUrl={"/images/contests.png"} />
            <Link to={{pathname: "/createevent", state: {type: "Contest", client: client}}}>Create New Contest</Link>
            <fieldset>
                {contests.map(contest => <EventCard name={contest.name} key={contest._id} date={contest.eventDate} />)}
            </fieldset>
        </>
    )
}

export default Contests
