import { useState, useEffect } from "react";
import { useLocation } from "react-router";
import axios from 'axios'

import Search from "../components/Search";
import MemberTag from "../components/MemberTag";

const userAxios = axios.create()
userAxios.interceptors.request.use(config => {
    const token = localStorage.getItem("token")
    config.headers.Authorization = `Bearer ${token}`
    return config
})

export default function AddTeamMember(){

    const location = useLocation()
    const { team, client} = location.state
    console.log(`Team`, team)
    console.log(`Client`, client)

    const [users, setUsers] = useState([])
    const [searchQuery, setSearchQuery] = useState('')
    const [selectedMembers, setSelectedMembers] = useState([])

    function getClientUsers(){
        userAxios(`/api/user/client/${client._id}`)
            .then(res => setUsers(res.data))
            .catch(err => console.log(err))
    }

    function handleSearch(){
        const filteredUsers = users.filter(user => {
            if(!user.firstName){
                return
            }
            const userName = `${user.firstName.toLowerCase()} ${user.lastName.toLowerCase()}`
            return userName.includes(searchQuery.toLowerCase())
        })

        return (
            <div>
                {filteredUsers.map(user => <MemberTag name={`${user.firstName} ${user.lastName}`} avatar={user.avatar} set={() => console.log('hello')} />)}
            </div>
        )
    }

    useEffect(() => {
        getClientUsers()
    }, [])

    return (
        <>
        <Search />
        {searchQuery.length > 0 ? handleSearch() : null}
        </>
    )
}
