import { useContext, useEffect } from "react"
import { UserContext } from "../context/UserProvider"
import SubHeader from "../components/SubHeader"
import {Link} from "react-router-dom"
import axios from "axios"
const userAxios = axios.create()
userAxios.interceptors.request.use(config => {
    const token = localStorage.getItem("token")
    config.headers.Authorization = `Bearer ${token}`
    return config
})

const Event = () => {

    const {user: {client, role}} = useContext(UserContext)

    const [events, setEvents] = useState([])

    function showEvents(events){
        const onlyEvents = events.filter(event => event.type === "event")
        setEvents(onlyEvents)
    }

    function getEvents(){

        if(role === "admin"){
            console.log('admin')
            userAxios.get("/event")
                .then(res => showEvents(res.data))
                .catch(err => console.log(err))
        } else {
            console.log('general')
            userAxios.get(`/event/${client}`)
                .then(res => showEvents(res.data))
                .catch(err => console.log(err))
        }

    }

    useEffect(() => {
        getEvents()
    }, [])

    return(
        <>
            <SubHeader header1={"SCHEDULE OF"} header2={"EVENTS"} imgUrl={"/images/events.png"} color={"#1c3557"} />
            <Link to={{pathname: "/createevent", state: {type: "Event", client: client}}}>Create New Event</Link>
            Events
        </>
    )
}

export default Event
