import { Link } from "react-router-dom"
import "../App.scss"

const TeamTag = (props) => {

    const {name, avatar, team, client} = props

    return (
            <Link className='tag' to={{
                pathname: '/team',
                state: {
                    team: team,
                    client: client
                }
            }}>
            <h2>{name}</h2>
            <img src={avatar} alt={avatar} style={{width: '200px', height: '200px',}} />
            </Link>
    )
}

export default TeamTag
