import { Link } from "react-router-dom"

const MemberTag = (props) => {

    const {name, avatar, member, role, set} = props

    const [toggle, setToggle] = useState(false)


    if(set === 'undefined'){
       return set = console.log('Nothing was assigned to props.set in MemberTag')
    }

    console.log(avatar)

    return (
        <div style={{border: `2px solid ${role === 'commissioner' ? 'green' : 'black'}`, padding: '20px'}} onClick={() => set()}>
            <Link to={{
                pathname: '/member',
                state: {
                    member: member
                }
            }}>
