import { Link } from "react-router-dom"
import "../App.scss"

const TeamTag = (props) => {

    const {name, avatar, team, client} = props

    return (
        <>
        <h2>{name}</h2>
            <Link className='tag' to={{
                pathname: '/team',
                state: {
                    team: team,
                    client: client
                }
            }}>
            <img src={avatar} alt={avatar} style={{width: '145x', height: '125x', borderRadius: '50%'}} />
            </Link>
        </>
    )
}

export default TeamTag
