import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios'
import { useLocation, Redirect } from 'react-router';

import TeamTag from '../components/TeamTag'
import "../client.scss"
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import CancelIcon from '@material-ui/icons/Cancel';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import Header from "../components/Header";
import SubHeader from '../components/SubHeader'
import FooterNavbar from "../components/FooterNavbar";

const userAxios = axios.create()
userAxios.interceptors.request.use(config => {
    const token = localStorage.getItem("token")
    config.headers.Authorization = `Bearer ${token}`
    return config
})

const AdminClient = () => {

    const location = useLocation()
    const {client} = location.state

    const [commissioner, setCommissioner] = useState({})
    const [teams, setTeams] = useState([])
    const [toggle, setToggle] = useState(false)

    function deleteClient(_id){
        userAxios.delete(`/api/client/${client._id}`)
          .then(res => console.log(res))
          .catch(err => console.log(err))
    }

    function editClient(inputs, _id){
        userAxios.put(`/api/client/${client._id}`, inputs)
            .then(res => console.log(res))
            .catch(err => console.log(err))
    }

    function handleToggle(){
        setToggle(prevToggle => !prevToggle)
    }

    function getTeams(){
        userAxios.get(`/api/team/client/${client._id}`)
            .then(res => {
                setTeams(res.data)
            })
            .then(<Redirect to='/profile' />)
            .catch(err => console.log(err))
    }

    function getCommissioner(){
        userAxios.get(`/api/user/${client.commissioner}`)
            .then(res => setCommissioner(res.data))
            .catch(err => console.log(err))
    }

    useEffect(() => {

        getCommissioner()
        getTeams()

    }, [])

    return (
        <>
        <Header />
        <SubHeader header1={client.name} header2={'TEAMS'}/>
        {!toggle ? <MoreHorizIcon onClick={handleToggle} /> :         
        <div className="action-tool" style={{border: '2px solid black'}}>
            <CancelIcon className="close" onClick={handleToggle}/>
            <div className="delete">
                <DeleteIcon onClick={() => console.log('Delete')} />
                <p>Delete Client</p>
            </div>
            <div className="">
                <EditIcon onClick={() => console.log('Edit')}/>
                <p>Edit Client</p>
            </div>
        </div>}
        <div className="comm-container">
            <p>
                Commissioner: {commissioner.firstName === undefined ? 'No commissioner assigned' : `${commissioner.firstName} ${commissioner.lastName}`}
            </p>
            <div style={{margin: '16px', display: 'flex', placeItems: 'center'}}>
                {teams.map(team => <TeamTag 
                                        key={team._id} 
                                        team={team} 
                                        client={client} 
                                        name={team.name} 
                                        avatar={team.avatar} 
                                    />
                )}

                <Link to={{
                    pathname: '/createteam',
                    state: {
                        client
                    }
                }}
                        style={{
                            width: '145px', 
                            height: '145px', 
                            borderRadius: '50%', 
                            boxShadow: '0 0 10px blue', 
                            display: 'grid', 
                            placeItems: 'center', 
                            color: '#1c3557', 
                            fontSize: '24px', 
                            textAlign: 'center', 
                            textDecoration: 'none'
                        }}
                >
                + NEW TEAM
                </Link>
            </div>
            <FooterNavbar />
        </div>
        </>
    )
}

export default AdminClient
