import { useState, useEffect } from 'react'
import axios from 'axios'
import { useLocation } from 'react-router'

import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import TeamTag from '../components/TeamTag'
import TeamForm from '../components/TeamForm'
import "../client.scss"
import Header from "../components/Header";

const userAxios = axios.create()
userAxios.interceptors.request.use(config => {
    const token = localStorage.getItem("token")
    config.headers.Authorization = `Bearer ${token}`
    return config
})

const Client = () => {

    const location = useLocation()
    const { client } = location.state

    const [commissioner, setCommissioner] = useState({})
    const [teams, setTeams] = useState([])

    function getTeams(){
        userAxios.get(`/api/team/client/${client._id}`)
            .then(res => {
                setTeams(res.data)
            })
            .catch(err => console.log(err))
    }

    function createTeam(e, inputs){
        e.preventDefault()

        userAxios.post(`/api/team/client/${client._id}`, inputs)
            .then(res => {
                setTeams(prevTeams => ([...prevTeams, res.data]))
            })
            .catch(err => console.log(err))
    }

    function getCommissioner(){
        userAxios.get(`/api/user/${client.commissioner}`)
            .then(res => setCommissioner(res.data))
            .catch(err => console.log(err))
    }

    useEffect(() => {

        getCommissioner()
        getTeams()

    }, [])

    return (
        <>
        <Header />
        <div className="client-header">
            <p><ArrowBackIosIcon />{client.name}</p>
        </div>
        <div className="comm-container">
            <p>Commissioner: {commissioner === 'undefined '? 'No commissioner assigned' : `${commissioner.firstName} ${commissioner.lastName}`}</p>
            <label>Add Team</label>
            <TeamForm submit={createTeam}/>
            {teams.length > 0 ? teams.map(team => <TeamTag key={team._id} team={team} client={client.name} name={team.name} avatar={team.avatar} />) : <p>Currently no teams in this client</p>}
        </div>

        </>
    )
}

export default Client
